mapper.insert:
INSERT INTO board (

) VALUES (
        #{idx}
    , #{title}
    , #{content}
    , #{writer}
    , 0
    , IFNULL(#{noticeYn}, 'N')
    , IFNULL(#{secretYn}, 'N')
    , 'N'
    , NOW()
    , NULL
    , NULL
)

mapper.update:
UPDATE board
SET
      update_time = NOW()
    , title = #{title}
    , content = #{content}
    , writer = #{writer}
    , IFNULL(#{noticeYn}, 'N')
    , IFNULL(#{secretYn}, 'N')
WHERE
    idx = #{idx}
